import React, { useState, useRef } from 'react';
import '../styles/Header.scss';
import CATEGORIES from './CategoryList';
import {Link} from 'react-router-dom'

function Header(){
    function category(e){
        console.log(e);
    }
        return(
            <div>
                <div className='header-inner'>
                    <div className="title">커뮤니티</div>
                    <div className="slider">
                    <ul className="category">
                        <li onClick="category('all')">전체</li>
                        <li onClick="category('best')">
                            <img className="star" alt="star"src={ require('../imgs/star.png')} />
                             인기글
                        </li>
                        {
                            CATEGORIES.map(function(list, index){
                                return(
                                    <li key={index} onClick={category(index)}>{list.categoryName}</li>
                                )
                            })
                        }                        
                    </ul>
                    </div>
                    <Link to='/community/post/new'>
                        <div className="write-button">
                            글쓰기
                            <img className="write" alt="write" src={ require('../imgs/write.png')} />
                        </div>
                    </Link>
                </div>
            </div>
        )
    // })
}
export default Header;
