import React from 'react';
import '../styles/Card.scss';
import PostList from '../components/PostList';

function Card(){
        return(
            <div>
                <div className="card-inner">
                    <ul>
                        {
                            PostList.map(function(list){
                                return(
                                    <li>
                                        <div className="profile">
                                            <div className="profile-bg">
                                                <img className="smile" alt="smile" src={ require('../imgs/shy-smile.png')} />
                                            </div>
                                        <div className="inform">
                                            <div className="nick-name">{PostList[list].writerNickName}</div>
                                            <div className="condition">{PostList[list].categoryName} • 1분전</div>
                                        </div>
                                        </div>
                                        <div className="post">
                                            <div className="post-title">{PostList[list].title}</div>
                                            <div className="post-content">{PostList[list].content}</div>
                                            <img className="post-img" alt="post-img" src={ require('../imgs/shy-smile.png')} />
                                                <ul className="post-state">
                                                    <li>
                                                        <img className="eye" alt="eye" src={ require('../imgs/eye.png')} />
                                                        {PostList[list].viewCount}
                                                    </li>
                                                    <li>
                                                        <img className="thumb" alt="thumb" src={ require('../imgs/thumb.png')} />
                                                        {PostList[list].likeCount}
                                                    </li>
                                                    <li>
                                                        <img className="talk" alt="talk" src={ require('../imgs/talk.png')} />
                                                        {PostList[list].commentCount}
                                                    </li>
                                                </ul>
                                            </div>
                                    </li>
                                )
                            })
                        }  
                    </ul>
                    
                </div>
            </div>
        )
}
export default Card;
