import React, { useState, useRef } from 'react';
import '../styles/Header.scss';

function Header(){
    const CATEGORIES = [
        {
            categoryPk: 1,
            categoryCode: 'PETITION',
            categoryName: '대선청원'
        },
        {
            categoryPk: 2,
            categoryCode: 'FREE',
            categoryName: '자유글'
        },
        {
            categoryPk: 3,
            categoryCode: 'QNA',
            categoryName: '질문/답변'
        },
        {
            categoryPk: 4,
            categoryCode: 'NEWS',
            categoryName: '뉴스'
        },
        {
            categoryPk: 5,
            categoryCode: 'TIP',
            categoryName: '노하우'
        }
    ]
    return(
        <div>
            <div>
                <div className="title">커뮤니티</div>
                <ul className="category">
                    <li>전체</li>
                    <li>
                        <img className="star" alt="star" src="imgs/star.png" />
                         인기글
                    </li>
                    <li>{CATEGORIES[0].categoryName}</li>
                    <li>{CATEGORIES[1].categoryName}</li>
                    <li>{CATEGORIES[2].categoryName}</li>
                    <li>{CATEGORIES[3].categoryName}</li>
                    <li>{CATEGORIES[4].categoryName}</li>
                </ul>
                <div className="write-button">
                    글쓰기
                    <img className="write" alt="write" src="imgs/write.png" />
                </div>
            </div>
        </div>
    )
}
export default Header;
