import React from 'react';
import '../styles/Card.scss';
import PostList from '../components/PostList';
import moment from 'moment';
import 'moment/locale/ko';

function Card(){
    //현재 날짜 및 시간
    function timeToday(value){
        // const now = moment();
        // const today = new Date();
        const today = moment();
        const date = moment(today);
        const standard = moment(value);
        // console.log(today.format("YY-MM-DD"));
        console.log(moment(standard).diff(moment(), 'days'));//2일전 , -2 출력
        if(moment(standard).diff(moment(), 'days')<0){
            console.log(today.format("YY-MM-DD"));
        }else{
            console.log(moment(standard).fromNow());
        }

        // if(moment(standard).diff(moment(), 'days')<0){
        //     return ` ${moment(standard).fromNow()}`;
        // }else{
        //     return ` ${value.substr(2,3)+value.substr(5,5)}`;
        // }
    }
    
    return(
        <div>
            <div className="card-inner">
            {
                PostList.map(function(list, index){
                    return(
                            <ul className='card' key={index}>
                                <li>
                                    <div className="profile">
                                        <div className="profile-bg">
                                            <img className="smile" alt="smile" src={ require('../imgs/shy-smile.png')} />
                                        </div>
                                    <div className="inform">
                                        <div className="nick-name">{list.writerNickName}</div>
                                        {timeToday(list.writtenAt)}
                                        {
                                            list.writtenAt?(<div className="condition">{list.categoryName} • {list.writtenAt.substr(2,3)}{list.writtenAt.substr(5,5)}</div>)
                                            :(<div className="condition">{list.categoryName} • {list.writtenAt}</div>)
                                        }
                                        
                                    </div>
                                    </div>
                                    <div className="post">
                                        <div className="post-title">{list.title}</div>
                                        {
                                            list.content.length<=50? ( //본문내용 생략
                                                <div className="post-content">{list.content}</div>
                                            ) : (
                                                <div className="post-content">{list.content.substr(0,50)+'...'}</div>
                                            )
                                        }
                                        {
                                            list.imageUrl? ( //본문 이미지 출력
                                            <img className="post-img" alt="post-img" src={list.imageUrl} />
                                            ) : (
                                                <div></div>
                                            )
                                        }
                                        <ul className="post-state">
                                            <li>
                                                <img className="eye" alt="eye" src={ require('../imgs/eye.png')} />
                                                {list.viewCount}
                                            </li>
                                            <li>
                                                <img className="thumb" alt="thumb" src={ require('../imgs/thumb.png')} />
                                                {list.likeCount}
                                            </li>
                                            <li>
                                                <img className="talk" alt="talk" src={ require('../imgs/talk.png')} />
                                                {list.commentCount}
                                            </li>
                                        </ul>
                                    </div>
                                </li>
                            </ul>
                            )
                        })
                    }  
            </div>
        </div>
    )
}
export default Card;
